const btn=document.querySelector(".video__btn"),video=document.getElementById("video"),modal=document.querySelector(".modal"),sliderItemBtn=document.querySelectorAll(".slider__item-btn"),modalImage=document.getElementById("modal-image"),modalPicture=document.querySelector(".modal__body picture source");function playVideo(){video.setAttribute("src","https://www.youtube.com/embed/FyKWUTwSYAs?autoplay=1"),video.style.zIndex=1}function showModal(e){document.body.style.overflow="hidden",modal.classList.add("active"),modalPicture.setAttribute("srcset",`img/picture${e+1}.webp`),modalImage.setAttribute("src",`img/picture${e+1}.jpg`)}function closeModal(){document.body.style="",modal.classList.remove("active")}btn.addEventListener("click",playVideo),sliderItemBtn.forEach((e,t)=>{e.addEventListener("click",()=>{showModal(t)})}),modal.addEventListener("click",e=>{e.target.classList.contains("active")&&closeModal()});const sliderList=document.querySelector(".slider__list"),slides=document.querySelectorAll(".slider__item"),slidesLength=slides.length,next=document.querySelector(".slider__btn--next"),prev=document.querySelector(".slider__btn--prev");let index=0,visibleSlides=3,sliderWidth=sliderList.offsetWidth,slideWidth=sliderWidth/slidesLength,initialPosition;function switchSlide(e){sliderList.classList.add("transition"),initialPosition=sliderList.offsetLeft,"next"===e?(sliderList.style.left=`${initialPosition-slideWidth}px`,index++):(sliderList.style.left=`${initialPosition+slideWidth}px`,index--)}function checkIndex(){sliderList.classList.remove("transition"),index<0&&(sliderList.style.left=`-${(slidesLength-visibleSlides)*slideWidth}px`,index=slidesLength-visibleSlides),index>slidesLength-visibleSlides&&(sliderList.style.left="0px",index=0)}next.addEventListener("click",()=>switchSlide("next")),prev.addEventListener("click",()=>switchSlide("prev")),sliderList.addEventListener("transitionend",checkIndex);